mapper.delete:
DELETE FROM notemodel where uniqueId IN (

    #{uniqueId}

)

update.foreach:
UPDATE notemodel
createdAt = #{item.createdAt}, updatedAt = #{item.updatedAt}, uniqueId = #{item.uniqueId}, parentUniqueId = #{item.parentUniqueId}, subPageName = #{item.subPageName}, title = #{item.title}, extraAttributes = #{item.extraAttributes}, `type` = #{item.type}, strokeWidth = #{item.strokeWidth}, eraserWidth = #{item.eraserWidth}, noteBackground = #{item.noteBackground}, strokeColor = #{item.strokeColor}, currentShapeType = #{item.currentShapeType}, lineLayoutBackground = #{item.lineLayoutBackground}, `position` = #{item.position}, pageNameList = #{item.pageNameList}, pageOriginWidth = #{item.pageOriginWidth}, pageOriginHeight = #{item.pageOriginHeight}, source = #{item.source}, pin = #{item.pin}, asyncStatus = #{item.asyncStatus}
where uniqueId = #{item.uniqueId} AND pin = #{item.pin}
